const plumJar = {};

let sugar = 0;

while (sugar < 100) {
  sugar++;
  if (sugar % 10 === 0) {
    plumJar[`plum-${sugar / 10}`] = 30;
  }
}

if (Object.keys(plumJar).length >= 10) {
  plumJar.liquor = 'refined soju';
  plumJar.sealed = true;
  console.log('Jar sealed. Aging begins...');
}

const waitOneYear = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve('Miuju has aged gracefully for one year.');
    }, 1000);
  });
};

(async () => {
  const msg = await waitOneYear();
  console.log(msg);
  console.log('Cheers! üç∂');
})();
